import React, { useState, useEffect } from 'react';
import './App.css';
import useColors from './hooks/useColors';
import axios from 'axios';

const App = () => {
	const [players, setPlayers] = useState([]);
	const [searchTerm, setSearchTerm] = useState('');
	const [searchResult, setSearchResult] = useState([]);
	const [counties, setCountries] = useState([]);

	useEffect(() => {
		const fetchData = async () => {
			const result = await axios('http://localhost:5000/api/players');
			counties.length > 0 ? setPlayers(counties) : setPlayers(result.data);
			searchResult.length > 0 ? setPlayers(searchResult) : setPlayers(result.data);
		};
		fetchData();
	}, [searchResult, counties]);

	const handleChange = e => {
		setSearchTerm(e.target.value);
	};

	const handleSearch = e => {
		e.preventDefault();
		const searchResult = players.filter(player =>
			player.name.toLowerCase().includes(searchTerm.toLowerCase())
		);
		setSearchResult(searchResult);
		setSearchTerm('');
	};

	const [color, setColor] = useColors(false);
	const toggleMode = e => {
		e.preventDefault();
		setColor(!color);
	};

	function filterByCountry(value) {
		let country = value;
		console.log(country);
		const searchResult = players.filter(player =>
			player.country.includes(country)
		);

		setCountries(searchResult);
	}

	return (
		<div>
			<h1>List of players</h1>
			<form onSubmit={handleSearch} data-testid="form">
				<input
					type="text"
					onChange={handleChange}
					placeholder="Type name..."
					value={searchTerm}
				/>
				<button>Search</button>
				<label>
					Toggle color
					<input type="checkbox" onClick={toggleMode} data-testid="toggle" />
				</label>
			</form>
			<p></p>
			<ul data-testid="list-of-players">
				{players.length > 0 ? (
					players.map(player => (
						<li key={player.id}>
							<p>{player.name}</p>
							<a href="#" onClick={() => filterByCountry(player.country)}>
								#{player.country.toLowerCase()}
							</a>
						</li>
					))
				) : (
					<p>Loading data...</p>
				)}
			</ul>
		</div>
	);
};

export default App;
