import styled from 'styled-components';

export const HeaderElement = styled.header`
	box-shadow: 0 2px 2px -2px rgba(0, 0, 0, 0.2);
	padding-bottom: 20px;
`;

export const Title = styled.h1`
	text-align: center;
	text-transform: uppercase;
	font-size: 1.5rem;
	padding: 2rem;
	letter-spacing: 1px;
`;

export const Button = styled.button`
	padding: 5px 25px;
	border-radius: 5px;
	margin-left: 10px;
	cursor: pointer;
	border: ${(props) =>
		props.search ? '1px solid lighgray' : '1px solid transparent'};
	background: ${(props) => (props.search ? 'transparent' : 'lightblue')};
`;

export const Label = styled.label`
	padding: 1rem;
	display: flex;
	align-items: center;
`;
