import styled from 'styled-components';

export const HeaderElement = styled.header`
	box-shadow: 0 2px 2px -2px rgba(0, 0, 0, 0.2);
	padding-bottom: 20px;
	margin-top: 0;
`;

export const Title = styled.h1`
	text-align: center;
	text-transform: uppercase;
	font-size: 1.5rem;
	padding: 2rem;
	letter-spacing: 1px;
`;

export const FormWrapper = styled.form`
	width: 50%;
	margin: 0 auto;
	display: flex;
	justify-content: space-around;
	align-items: center;
	border: 1px solid red;
`;

export const Input = styled.input`
	border: none;
	padding: 5px;
	&[type='text'] {
		flex-grow: 2;
	}
`;

export const Button = styled.button`
	padding: 5px 35px;
	border-radius: 5px;
	margin-left: 10px;
	cursor: pointer;
	border: ${(props) =>
		props.search ? '1px solid lighgray' : '1px solid transparent'};
	background: ${(props) => (props.search ? 'transparent' : 'lightblue')};
`;

export const Label = styled.label`
	padding: 1rem;
	display: flex;
	align-items: center;
	font-size: 1.3rem;
	flex-grow: 2;
`;
