import styled from 'styled-components';

export const Title = styled.h1`
	text-align: center;
	text-transform: uppercase;
	font-size: 1.5rem;
	padding: 2rem;
	letter-spacing: 1px;
`;

export const Button = styled.button`
	width: 85px;
	padding: 15px;
	border-radius: 5px;
	margin-left: 10px;
	cursor: pointer;
`;
