import React from 'react';
import './App.css';
import useColors from './hooks/useColors';
import { Title, Button, Label } from './styles';

const Header = (props) => {
	return (
		<header>
			<Title>List of players</Title>
			<form onSubmit={props.handleSearch} data-testid="form">
				<input
					type="text"
					onChange={props.handleChange}
					placeholder="Type name..."
					value={props.searchTerm}
				/>
				<Button search>Search</Button>
				<Button onClick={props.clearFilterByCountry}>Clear</Button>
				<Label>
					Toggle color
					<input type="checkbox" onClick={props.toggleMode} data-testid="toggle" />
				</Label>
			</form>
		</header>
	);
};

export default Header;
